import axios from "axios";

const BLOGS = "BLOGS";
const ADD_BLOG = "ADD_BLOG";
const UPDATE_BLOG = "UPDATE_BLOG";
const DELETE_BLOG = "DELETE_BLOG";

// REDUX ACTIONS

export const getBlogs = (cb) => {
  return (dispatch) => {
    axios.get("/api/blogs")
      .then( res => dispatch({ type: BLOGS, blogs: res.data }) )
      .then( cb() )
  }
}

export const addBlog = (blog) => {
  return (dispatch) => {
    axios.post("/api/blogs", { blog, } )
      .then( res => dispatch({ type: ADD_BLOG, blog: res.data }) )
  }
}

export const updateBlog = (blog) => {
  return (dispatch) => {
    axios.put(`/api/blogs/${blog.id}`, { blog, })
      .then( res => dispatch({ type: UPDATE_BLOG, blog: res.data, }))
  }
}

export const deleteBlog = (id) => {
  return (dispatch) => {
    axios.delete(`/api/blogs/${id}`)
      .then( () => dispatch({ type: DELETE_BLOG, id }) )
  }
}

// REDUX REDUCER

export default (state = [], action, ) => {
  switch(action.type) {
    case BLOGS:
      return action.blogs
    case ADD_BLOG:
      return [action.blog, ...state]
    case UPDATE_BLOG:
      return state.map( a => {
        if (b.id === action.blog.id)
          return action.blog
         return a
      })
    case DELETE_BLOG:
      return state.filter( b => b.id !== action.id )
    default:
      return state;
  }
}
